$(document).ready(function () {
  $('.company-navbar').change(function () {
    if (verifyIfCompanyIsDefault($(this).val())) return;
    $('#integration-actions').hide();
    $("#no-integration-found").hide();
    $('#project-empty').hide();
    loadOnAny('#content');
    updateCompanyDefault().done(function (data1) {
      getCompaniesAndProjects().done(function (data2) {
        companiesAndProjects = data2;
        index('n');
      });
    });
  });
  var companiesAndProjects = '';
  getCompaniesAndProjects().done(function (data) {
    companiesAndProjects = data;
    index();
  });

  function index() {
    var loading = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : 'y';
    if (loading == 'y') loadingOnScreen();else {
      $("#content").html("");
      loadOnAny('#content');
    }
    $hasProjects = false;

    if (companiesAndProjects.company_default_projects) {
      $.each(companiesAndProjects.company_default_projects, function (i, project) {
        if (project.status == 1) $hasProjects = true;
      });
    }

    if (!$hasProjects) {
      $('#integration-actions').hide();
      $("#no-integration-found").hide(); //$('#project-integrated').hide();

      $('#project-empty').show();
      loadingOnScreenRemove();
      loadOnAny('#content', true);
    } else {
      $.ajax({
        method: "GET",
        url: "/api/apps/activecampaign?company=" + $('.company-navbar').val(),
        dataType: "json",
        headers: {
          'Authorization': $('meta[name="access-token"]').attr('content'),
          'Accept': 'application/json'
        },
        error: function error(response) {
          loadOnAny('#content', true);
          loadingOnScreenRemove();
          errorAjaxResponse(response);
        },
        success: function success(response) {
          $('#project_id, #select_projects_edit').html("");
          fillSelectProject(companiesAndProjects, '#project_id, #select_projects_edit');

          if (isEmpty(response.integrations)) {
            $("#no-integration-found").show();
          } else {
            $('#content').html("");
            var integrations = response.integrations;

            for (var i = 0; i < integrations.length; i++) {
              renderIntegration(integrations[i]);
            }

            $("#no-integration-found").hide();
          }

          $('#project-empty').hide();
          $('#integration-actions').show(); // }

          if (loading == 'y') loadingOnScreenRemove();
          loadOnAny('#content', true);
        }
      });
    }
  } //checkbox


  $(".check").on("click", function () {
    if ($(this).is(":checked")) {
      $(this).val(1);
    } else {
      $(this).val(0);
    }
  }); //reset the intergation modal

  function clearForm() {
    $(":text").val("");
    $(":checkbox").prop("checked", true).val(1);
    $("#project_id, #select_projects_edit").prop("selectedIndex", 0).change();
  } //draw the integration cards


  function renderIntegration(data) {
    $("#content").append("\n                            <div class=\"col-sm-6 col-md-4 col-lg-3 col-xl-3\">\n                                <div class=\"card shadow card-edit\" project=" + data.id + " style='cursor:pointer;'>\n                                    <a href=\"/apps/activecampaign/".concat(data.id, "\" class=\"activecampaign-link\">\n                                        <img class=\"card-img-top img-fluid w-full\" src=") + data.project_photo + " onerror=\"this.onerror=null;this.src='/build/global/img/produto.png';\" alt=\"" + data.project_name + "\"/>\n                                    </a>\n                                    <div class=\"card-body\">\n                                        <div class='row'>\n                                            <div class='col-md-10'>\n                                                <a href=\"/apps/activecampaign/".concat(data.id, "\" class=\"activecampaign-link\">\n                                                    <h4 class=\"card-title\">") + data.project_name + "</h4>\n                                                    <p class=\"card-text sm\">Criado em " + data.created_at + "</p>\n                                                </a>\n                                            </div>\n                                            <div class='col-md-2'>\n                                                <div role='button' title='Excluir' class='delete-integration pointer float-right mt-35' project=" + data.id + ">\n                                                    <span class='o-bin-1'></span>\n                                                </div>\n                                            </div>\n                                        </div>\n                                    </div>\n                                </div>\n                            </div>\n                        ");
  } //create


  $("#btn-add-integration").on("click", function () {
    $(".modal-title").html("Adicionar nova Integração com ActiveCampaign");
    $("#bt_integration_add").addClass("btn-save");
    $("#bt_integration_add").removeClass("btn-update");
    $("#bt_integration_add").text("Adicionar integração");
    $("#modal_add_integracao").modal("show");
    $("#form_update_integration").hide();
    $("#form_add_integration").show();
    clearForm();
  }); //store

  $(document).on("click", ".btn-save", function () {
    if ($("#link").val() == "") {
      alertCustom("error", "Dados informados inválidos");
      return false;
    }

    var form_data = new FormData(document.getElementById("form_add_integration"));
    $.ajax({
      method: "POST",
      url: "/api/apps/activecampaign",
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      processData: false,
      contentType: false,
      cache: false,
      data: form_data,
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        index();
        alertCustom("success", response.message);
      }
    });
  }); //update

  $(document).on("click", ".btn-update", function () {
    if ($("#link_edit").val() == "") {
      alertCustom("error", "Dados informados inválidos");
      return false;
    }

    var integrationId = $("#integration_id").val();
    var form_data = new FormData(document.getElementById("form_update_integration"));
    $.ajax({
      method: "POST",
      url: "/api/apps/activecampaign/" + integrationId,
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      processData: false,
      contentType: false,
      cache: false,
      data: form_data,
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        index();
        alertCustom("success", response.message);
      }
    });
  }); // load delete modal

  $(document).on("click", ".delete-integration", function (e) {
    e.stopPropagation();
    var project = $(this).attr("project");
    var card = $(this).parent().parent().parent().parent().parent();
    card.find('.card-edit').unbind('click');
    $("#modal-delete-integration .btn-delete").attr("project", project);
    $("#modal-delete-integration").modal("show");
  }); //destroy

  $(document).on("click", "#modal-delete-integration .btn-delete", function (e) {
    e.stopPropagation();
    var project = $(this).attr("project");
    $.ajax({
      method: "DELETE",
      url: "/api/apps/activecampaign/" + project,
      dataType: "json",
      headers: {
        'Authorization': $('meta[name="access-token"]').attr('content'),
        'Accept': 'application/json'
      },
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        index();
        alertCustom("success", response.message);
      }
    });
  });
});
