$(document).ready(function () {
  $(".company-navbar").change(function () {
    if (verifyIfCompanyIsDefault($(this).val())) return;
    $("#integration-actions").hide();
    $("#no-integration-found").hide();
    $("#project-empty").hide();
    loadOnAny("#content");
    updateCompanyDefault().done(function (data1) {
      getCompaniesAndProjects().done(function (data2) {
        companiesAndProjects = data2;
        index("n");
      });
    });
  });
  var companiesAndProjects = "";

  function index() {
    var loading = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : "y";
    if (loading == "y") loadingOnScreen();else loadOnAny("#content");
    $hasProjects = false;

    if (companiesAndProjects.company_default_projects) {
      $.each(companiesAndProjects.company_default_projects, function (i, project) {
        if (project.status == 1) $hasProjects = true;
      });
    }

    if (!$hasProjects) {
      $("#integration-actions").hide();
      $("#no-integration-found").hide();
      $("#project-empty").show();
      loadingOnScreenRemove();
      loadOnAny("#content", true);
    } else {
      $.ajax({
        method: "GET",
        url: "/api/apps/smartfunnel?company=" + $(".company-navbar").val(),
        dataType: "json",
        headers: {
          Authorization: $('meta[name="access-token"]').attr("content"),
          Accept: "application/json"
        },
        error: function error(response) {
          loadOnAny("#content", true);
          loadingOnScreenRemove();
          errorAjaxResponse(response);
        },
        success: function success(response) {
          $("#project_id, #select_projects_edit").html("");
          fillSelectProject(companiesAndProjects, "#project_id, #select_projects_edit");

          if (isEmpty(response.integrations)) {
            $("#no-integration-found").show();
          } else {
            $("#content").html("");
            var integrations = response.integrations;

            for (var i = 0; i < integrations.length; i++) {
              renderIntegration(integrations[i]);
            }

            $("#no-integration-found").hide();
          }

          $("#project-empty").hide();
          $("#integration-actions").show();
          if (loading == "y") loadOnAny("#content", true);
          loadingOnScreenRemove();
        }
      });
    }
  }

  getCompaniesAndProjects().done(function (data) {
    companiesAndProjects = data;
    index();
  }); //checkbox

  $(".check").on("click", function () {
    if ($(this).is(":checked")) {
      $(this).val(1);
    } else {
      $(this).val(0);
    }
  }); //reset the intergation modal

  function clearForm() {
    $("#api_url").val("");
    $(":checkbox").prop("checked", true).val(1);
    $("#project_id, #select_projects_edit").prop("selectedIndex", 0).change();
  } //draw the integration cards


  function renderIntegration(data) {
    $("#content").append("\n                            <div class=\"col-sm-6 col-md-4 col-lg-3 col-xl-3\">\n                                <div class=\"card shadow card-edit\" project=" + data.id + " style='cursor:pointer;'>\n\n                <div class=\"d-flex align-items-center justify-content-center\" style=\"height:290px\" >\n                <img class=\"card-img-top img-fluid w-full\" src=" + data.project_photo + " onerror=\"this.onerror=null;this.src='/build/global/img/produto.svg';\" alt=\"" + data.project_name + "\"/>\n                </div>\n\n                                    <div class=\"card-body\">\n                                        <div class='row'>\n                                            <div class='col-md-10'>\n                                                <h4 class=\"card-title\">" + data.project_name + "</h4>\n                                                <p class=\"card-text sm\">Criado em " + data.created_at + "</p>\n                                            </div>\n                                            <div class='col-md-2'>\n                                                <a role='button' title='Excluir' class='delete-integration float-right mt-35' project=" + data.id + ">\n                                                    <img src=\"/build/global/img/icon-trash-new.svg\" />\n                                                </a>\n                                            </div>\n                                        </div>\n                                    </div>\n                                </div>\n                            </div>\n                        ");
  } //create


  $("#btn-add-integration").on("click", function () {
    $(".modal-title").html("Adicionar nova Integração com Smart Funnel");
    $("#bt_integration").addClass("btn-save");
    $("#bt_integration").removeClass("btn-update");
    $("#bt_integration").text("Adicionar integração");
    $("#modal_add_integracao").modal("show");
    $("#form_update_integration").hide();
    $("#form_add_integration").show();
    clearForm();
  }); //edit

  $(document).on("click", ".card-edit", function () {
    $(".modal-title").html("Editar Integração com Smart Funnel");
    $("#bt_integration").addClass("btn-update");
    $("#bt_integration").removeClass("btn-save");
    $("#bt_integration").text("Atualizar");
    $("#form_update_integration").show();
    $("#form_add_integration").hide();
    $("#modal_add_integracao").modal("show");
    $.ajax({
      method: "GET",
      url: "/api/apps/smartfunnel/" + $(this).attr("project"),
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        $("#select_projects_edit").val(response.data.project_id);
        $("#integration_id").val(response.data.id);
        $("#api_url_edit").val(response.data.api_url);
      }
    });
  }); //store

  $(document).on("click", ".btn-save", function () {
    if ($("#api_url").val() == "") {
      alertCustom("error", "Dados informados inválidos");
      return false;
    }

    var form_data = new FormData(document.getElementById("form_add_integration"));
    $.ajax({
      method: "POST",
      url: "/api/apps/smartfunnel",
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      processData: false,
      contentType: false,
      cache: false,
      data: form_data,
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        index();
        alertCustom("success", response.message);
      }
    });
  }); //update

  $(document).on("click", ".btn-update", function () {
    if ($("#api_url_edit").val() == "") {
      alertCustom("error", "Dados informados inválidos");
      return false;
    }

    var integrationId = $("#integration_id").val();
    var form_data = new FormData(document.getElementById("form_update_integration"));
    $.ajax({
      method: "POST",
      url: "/api/apps/smartfunnel/" + integrationId,
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      processData: false,
      contentType: false,
      cache: false,
      data: form_data,
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        index();
        alertCustom("success", response.message);
      }
    });
  }); //destroy
  // $(document).on("click", ".delete-integration", function (e) {
  //     $("#modal-delete-funel").modal();
  //     return false;
  // });

  $(document).on("click", ".delete-integration", function (e) {
    e.stopPropagation();
    var project = $(this).attr("project");
    var card = $(this).parent().parent().parent().parent().parent();
    card.find(".card-edit").unbind("click");
    $.ajax({
      method: "DELETE",
      url: "/api/apps/smartfunnel/" + project,
      dataType: "json",
      headers: {
        Authorization: $('meta[name="access-token"]').attr("content"),
        Accept: "application/json"
      },
      error: function error(response) {
        errorAjaxResponse(response);
      },
      success: function success(response) {
        alertCustom("success", response.message);
        location.reload();
      }
    });
  });
});
